import React, { useState, useEffect } from "react";
import '../css/global.css'
import Register from "./Register";
import LoginView from "./LoginView";
import Hash from "./Hash"

const Login = () => {
    // var flag = true;
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [pass, setPass] = useState("");
    const [pass2, setPass2] = useState("");

    const handleUsername = event =>{
      setUsername(event.target.value);
      console.log(username);
    }
    const handleEmail = event =>{
      setEmail(event.target.value);
    }
    const handlePassword = event =>{
      setPass(event.target.value);
    }
    const handlePassword2 = event =>{
      setPass2(event.target.value);
    }

    const createUser = async ()=>{
      if(pass == pass2){
        console.log("user = ", username);
        console.log("pass2 = ", pass2);
        const body = {
          username: username,
          password: Hash(pass)
        };
        const response = await fetch("http://localhost:5000/register", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body)
        });
        const myJson = await response.json();
        console.log(myJson)
      }
      setUsername("");
      setPass("");
    }

    const [flipper, setFlipper] = useState(false);
    const [page, setPage] = useState(<LoginView/>);
    const [button, setButton] = useState("Not a User? click here");
    const register = ()=>{
      if(flipper){
        console.log("normal");
        setPage(<LoginView/>);
        setButton("Not a User? click here");
        createUser();
        return false;
      }
      else{
        console.log("reg");
        setPage(<Register handleUsername={handleUsername}
        handlePassword={handlePassword}
        handlePassword2={handlePassword2}/>)
        setButton("Register");
        return true;
      }
    }


    return(
    <>
    {page}
    <div>
      <button onClick={()=>{
        setFlipper(register());}}>{button}</button>
    </div></>)
};

export default Login;
