import React, { useState, useEffect } from "react";
import '../css/global.css'
import Register from "./Register";
import LoginView from "./LoginView";
import Hash from "./Hash"

const Login = () => {
    // var flag = true;
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [pass, setPass] = useState("");
    const [pass2, setPass2] = useState("");

    const clear = ()=>{
      setUsername("");
      setEmail("");
      setPass("");
      setPass2("");
    }

    const handleUsername = event =>{
      setUsername(event.target.value);
      console.log(username);
    }
    const handleEmail = event =>{
      setEmail(event.target.value);
    }
    const handlePassword = event =>{
      setPass(event.target.value);
    }
    const handlePassword2 = event =>{
      setPass2(event.target.value);
    }

    const loginHandler = async ()=>{
      console.log("username: ", username);
      console.log("password: ", pass)
    }

    const createUser = async ()=>{
      if(pass == pass2){
        console.log("user = ", username);
        console.log("pass2 = ", pass2);
        const body = {
          username: username,
          password: Hash(pass),
          email: email
        };
        const response = await fetch("http://localhost:5000/register", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body)
        });
        const myJson = await response.json();
        console.log(myJson)
      }
    }

    const [flipper, setFlipper] = useState(false);
    const [page, setPage] = useState(<LoginView
      handleUsername={handleUsername}
      handlePassword={handlePassword}
      clear={clear}/>);
    const [button, setButton] = useState("Not a User? click here");
    const register = ()=>{
      if(flipper){
        console.log("normal");
        setPage(<LoginView/>);
        setButton("Not a User? click here");
        createUser();
        return false;
      }
      else{
        console.log("reg");
        setPage(<Register/>)
        setButton("Register");
        return true;
      }
    }

    const [loginBtn, setLoginBtn] = useState(<button onClick={loginHandler}>Login</button>)
    return(
    <>
    {page}
    <div>{loginBtn}</div>
    <div>
      <button onClick={()=>{
        setFlipper(register());}}>{button}</button>
    </div></>)
};

export default Login;
